package ewfwef;

public class TheAreaOfTheCircle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double area;
		double radius;
		
		radius = 20;
		
		area = radius * radius * 3.14;
		
		System.out.println("The area for the circle of radius" + "\t" + "面积" + radius + "is" + area);
		
	}

}
