package ewfwef;

public class Week_3_20_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a =4;
		System.out.println(factorial(a));
	}
	
		public static int factorial(int a) {
			int factorial = 1;
			for (int i =1; i <=a ; i++) {
				factorial *=i;
			}
				return factorial;
			}
			
	}
	

